import { Box, Button, ButtonGroup, Hidden, Stack, TextField, Typography, styled } from '@mui/material'
import React, { useState } from 'react'
import Theme from '../../Component/Theme';

const theme=Theme();

const UserTitleBox=styled(Stack)(({theme})=>({
    marginTop:"10px",
    marginBottom:"20px",
    width:500,
    [theme.breakpoints.down("md")]:{
        width:350,
    },
    //backgroundColor:"red",
    //paddingLeft:"100px",
    //paddingRight:"100px",
    textAlign:"center",

}))

const Styledstack=styled(Stack)(({theme})=>({
    width:500,
    [theme.breakpoints.down("md")]:{
        width:350,
    },
    textAlign:"center",
    alignItems:"center",
    justifyContent:"center"

}))

export default function Recovery({setRecoveryModel}) {

    const [email,setEmail]=useState(true);

    const [mobile,setMobile]=useState(false);

    const emailHandle=()=>{
        setEmail(true);
        setMobile(false);
    }

    const mobileHandle=()=>{
        setEmail(false);
        setMobile(true);
    }

  return (
    <Stack sx={{
        backgroundColor:theme.palette.background.paper,
        width:700,
        height:800,
        borderRadius:theme.shape.borderRadius,
        overflow:"hidden"
        }}>
        <Stack display="flex" direction="row" justifyContent='end'>
            <Button
                variant="outlined" 
                onClick={()=>setRecoveryModel(false)}
            ><Typography variant='h5'>X</Typography>
            </Button>
        </Stack>

        <Stack alignItems="center">
            <UserTitleBox bgcolor="red">
                <Typography variant="h3">Recovery Password</Typography>
            </UserTitleBox>
            <Styledstack>
                <Typography>Select a recovery method to proceed</Typography>
            </Styledstack>
            <Styledstack  direction="row" >
                <ButtonGroup>
                    <Button variant={email?'contained':"outlined"} sx={{width:150}} onClick={emailHandle}>Email</Button>
                    <Button variant={mobile?"contained":"outlined"} sx={{width:150}} onClick={mobileHandle}>Mobile</Button>
                </ButtonGroup>
            </Styledstack>
            <Styledstack>
                {email&&<Typography>We will send a OTP code for given email address to recover account</Typography>}
                {mobile&&<Typography>We will send a OTP code for given mobile number to recover account</Typography>}
            </Styledstack>

            <Styledstack>
            <TextField id="pwd" name="password" variant="outlined" size='small' placeholder="Enter your email"
                                    type="text"
                                    sx={{
                                        size:'small',
                                        width:"100%",
                                        borderRadius:theme.shape.borderRadius,
                                        "&:hover":{
                                            borderBlockColor:theme.palette.success.main
                                        }
                                        }}
                                    />
            </Styledstack>
        </Stack>

    </Stack>
  )
}
