import { Box, Button, IconButton, InputAdornment, Modal, Stack, TextField, Typography } from '@mui/material'
import React, { useEffect, useState } from 'react'
import Theme from '../../Component/Theme';
import styled from '@emotion/styled';
import { Visibility, VisibilityOff } from '@mui/icons-material';
import { validateNewPassword } from './Validation';

import ReactDOM from 'react-dom'

const theme=Theme();


const Overly=styled(Stack)(({})=>({
    position:"fixed",
    top:0,
    left:0,
    right:0,
    bottom:0,
    backgroundColor:'rgba(0,0,0, 0.7)',
    zIndex:1000
}))

const ModelStack=styled(Stack)(({})=>({
    zIndex:2000,

}))




const TitleBox=styled(Box)(({theme})=>({
    marginBottom:"30px",
    textAlign:"center",
    width:500,
    [theme.breakpoints.down("md")]:{
        width:350,
    }
}))

const UserText=styled(Stack)(({theme})=>({
    textAlign:"left",
    width:500,
    [theme.breakpoints.down("md")]:{
        width:350,
    }
}))

const UserInputBox=styled(Stack)(({theme})=>({
    textAlign:"left",
    width:500,
    [theme.breakpoints.down("md")]:{
        width:350,
    }
}))

export default function Reset({reset,setReset,openAlertSuccess}) {

    const [showPassword,setShowPassword]=useState(false);

    //validate
    const initialValue={newpassword:"",confirmpassword:""}
    const [formValues,setFormValues]=useState(initialValue);
    const [frormError,setFormError]=useState({});
    const [isSubmit,setIsSubmit]=useState(false);

    const handleNewPassword=(e)=>{
        setFormValues(e.target);
    }

    useEffect(()=>{
        if(Object.keys(formValues).length===0 && isSubmit){
            openAlertSuccess();
        }
    },[frormError])

    const resetPwdHandle=(e)=>{
        setFormError(validateNewPassword(formValues));
        setIsSubmit(true);

    }


  return ReactDOM.createPortal(
    <Overly>
        <Stack alignItems="center" justifyContent="center">
        <ModelStack sx={{
            backgroundColor:theme.palette.background.paper,
            marginTop:11,
            marginBottom:11,
            width:600,
            height:725,
            borderRadius:theme.shape.borderRadius,
            overflow:"hidden"
            }}
        >
            <Stack display="flex" direction="row" justifyContent='end'>
                    <Button
                        variant="contained" 
                        onClick={()=>setReset(false)}
                    ><Typography variant='h5'>X</Typography>
                    </Button>
            </Stack>
            <Stack display="flex" direction="column" alignItems="center">
                <TitleBox>
                    <Typography variant="h3">Reset Password</Typography>
                </TitleBox>

                <UserText>
                    <Typography variant="h6">Password must be:</Typography>
                </UserText>

                <Stack sx={{marginBottom:"20px"}}>
                    <ul>
                        <li><Typography variant="h6">Be at least 8 characters long</Typography></li>
                    <li> <Typography variant="h6">Contain at least one uppercase</Typography></li>
                        <li><Typography variant="h6">Contain at least one lowercase</Typography></li>
                        <li><Typography variant="h6">Contain at least one number</Typography></li>
                        <li><Typography variant="h6">Contain at least one special characters</Typography></li>
                    </ul>
                </Stack>
                <Stack display="flex" direction="column" spacing={2}>
                                    <UserText>
                                        <Typography variant='h5'>Enter New Password</Typography>
                                    </UserText>

                                    <UserInputBox>

                                        <TextField id="pwd" name="password" variant="outlined" size='small'
                                            helperText={frormError.newpassword}
                                            FormHelperTextProps={{ style: { color:theme.palette.error.main} }}
                                            value={formValues.newpassword}
                                            onChange={handleNewPassword}
                                            type={showPassword? "text":"password"}
                                            sx={{
                                                placeholder:"Enter your password",
                                                size:'small',
                                                width:"100%",
                                                borderRadius:theme.shape.borderRadius,
                                                "&:hover":{
                                                    borderBlockColor:theme.palette.success.main
                                                }
                                                }}
                                            InputProps={{

                                                    endAdornment: (

                                                    <InputAdornment position="end">

                                                        <IconButton

                                                        edge="end"

                                                        onClick={()=>setShowPassword(true)}

                                                        onBlur={()=>setShowPassword(false)}

                                                        >

                                                        {showPassword ? <Visibility /> : <VisibilityOff/>}

                                                        </IconButton>

                                                    </InputAdornment>

                                                    ),

                                            }}
                                        />
                                    </UserInputBox>

                                    <UserText>
                                        <Typography variant='h5'>Confirm New Password</Typography>
                                    </UserText>

                                    <UserInputBox>

                                        <TextField id="pwd" name="password" variant="outlined" size='small'
                                        helperText={frormError.confirmpassword}
                                        FormHelperTextProps={{ style: { color:theme.palette.error.main} }}
                                        value={formValues.confirmpassword}
                                        onChange={handleNewPassword}
                                        type={showPassword? "text":"password"}
                                        sx={{
                                            placeholder:"Confirm your password",
                                            size:'small',
                                            width:"100%",
                                            borderRadius:theme.shape.borderRadius,
                                            "&:hover":{
                                                borderBlockColor:theme.palette.success.main
                                            }
                                            }}
                                        InputProps={{

                                                endAdornment: (

                                                <InputAdornment position="end">

                                                    <IconButton

                                                    edge="end"

                                                    onClick={()=>setShowPassword(true)}

                                                    onBlur={()=>setShowPassword(false)}

                                                    >

                                                    {showPassword ? <Visibility /> : <VisibilityOff/>}

                                                    </IconButton>

                                                </InputAdornment>

                                                ),

                                        }}
                                        />
                                    </UserInputBox>

                                </Stack>

            <Stack justifyContent="center" alignItems="center" sx={{
                marginTop:5,
                width:500,
                [theme.breakpoints.down("md")]:{
                    width:350,
                },
            }}
            >
                <Button
                    variant="contained" 
                    sx={{
                        width:"100%",
                        "&:hover":{
                            backgroundColor:theme.palette.secondary.main,
                        }

                    }}
                    onClick={()=>resetPwdHandle()}
                >
                        <Typography variant='h6'>Reset Password</Typography>
                </Button>
            </Stack>
            </Stack>

        </ModelStack>
        </Stack>
    </Overly>,
    document.getElementById("portal-reset")
  )
}
