import { Button, ButtonGroup,Stack, TextField, Typography, styled } from '@mui/material'
import React, { useState } from 'react'
import Theme from '../../Component/Theme';
import Reset from './Reset';
import {Modal} from '@mui/material';
import ReactDOM from 'react-dom'

const theme=Theme();

const Overly=styled(Stack)(({})=>({
    position:"fixed",
    top:0,
    left:0,
    right:0,
    bottom:0,
    backgroundColor:'rgba(0,0,0, 0.7)',
    zIndex:1000
}))

const UserTitleBox=styled(Stack)(({theme})=>({
    marginTop:"50px",
    marginBottom:"30px",
    width:500,
    [theme.breakpoints.down("md")]:{
        width:350,
    },
    textAlign:"center",

}))

const Styledstack=styled(Stack)(({theme})=>({
    width:500,
    [theme.breakpoints.down("md")]:{
        width:350,
    },
    textAlign:"center",
    alignItems:"center",
    justifyContent:"center"

}))

const RecoveryModal=styled(Modal)(({theme})=>({
    display:"flex",
    justifyContent:"center",
    alignItems:"center",

}))

export default function Recovery({recoveryModel ,setRecoveryModel}) {

    const [email,setEmail]=useState(true);

    const [mobile,setMobile]=useState(false);

    const [reset,setReset]=useState(false);

    const emailHandle=()=>{
        setEmail(true);
        setMobile(false);
    }

    const mobileHandle=()=>{
        setEmail(false);
        setMobile(true);
    }

    const resetPasswordModelOn=()=>{
        setReset(true);

    }

  return ReactDOM.createPortal(
    <Stack sx={{
        backgroundColor:theme.palette.background.paper,
        width:600,
        height:725,
        borderRadius:theme.shape.borderRadius,
        overflow:"hidden",
        }}>
        <Stack display="flex" direction="row" justifyContent='end'>
            <Button
                variant="contained" 
                onClick={()=>setRecoveryModel(false)}
            ><Typography variant='h5'>X</Typography>
            </Button>
        </Stack>

        <Stack alignItems="center" spacing={3}>
            <UserTitleBox>
                <Typography variant="h3">Recovery Password</Typography>
            </UserTitleBox>
            <Styledstack>
                <Typography variant="h6">Select a recovery method to proceed</Typography>
            </Styledstack>
            <Styledstack  direction="row" >
                <ButtonGroup>
                    <Button variant={email?'contained':"outlined"} sx=
                    {{  width:150,
                        "&:hover":{
                            backgroundColor:theme.palette.secondary.main,
                        }
                    }} 
                    onClick={emailHandle}
                    >
                        <Typography variant='h6'>Email</Typography>
                    </Button>

                    <Button variant={mobile?"contained":"outlined"} sx=
                    {{  width:150,
                        "&:hover":{
                            backgroundColor:theme.palette.secondary.main,
                        }
                    }}
                     onClick={mobileHandle}
                    >
                        <Typography variant='h6'>Mobile</Typography>
                    </Button>
                </ButtonGroup>
            </Styledstack>
            <Styledstack>
                {email&&<Typography variant='h6'>We will send a OTP code for given email address to recover account</Typography>}
                {mobile&&<Typography variant='h6'>We will send a OTP code for given mobile number to recover account</Typography>}
            </Styledstack>

            <Styledstack>
            {email&&<TextField id="email" name="email" variant="outlined" size='small' placeholder="Enter your email"
                                    type="text"
                                    sx={{
                                        size:'small',
                                        width:"100%",
                                        borderRadius:theme.shape.borderRadius,
                                        "&:hover":{
                                            borderBlockColor:theme.palette.success.main
                                        }
                                        }}
                                    />}

            {mobile&&<TextField id="mobile-no" name="mobile-no" variant="outlined" size='small' placeholder="Enter your mobile number"
                                    type="text"
                                    sx={{
                                        size:'small',
                                        width:"100%",
                                        borderRadius:theme.shape.borderRadius,
                                        "&:hover":{
                                            borderBlockColor:theme.palette.success.main
                                        }
                                        }}
                                    />}
            </Styledstack>

            <Styledstack>
                <Button variant="contained" sx=
                {{
                    width:"100%",
                    "&:hover":{
                        backgroundColor:theme.palette.secondary.main,
                    }
                }}><Typography variant='h6'>Send OTP</Typography></Button>
            </Styledstack>

            <Stack spacing={2} textAlign="left" sx={{
                width:500,
                [theme.breakpoints.down("md")]:{
                    width:350,
                },
                }}>
                <Typography variant='h5'>Enter OTP</Typography>
            </Stack>

            <Styledstack>
            <TextField id="otp" name="otp" variant="outlined" size='small' placeholder="xxx-xxx"
                                    type="text"
                                    sx={{
                                        size:'small',
                                        width:"100%",
                                        borderRadius:theme.shape.borderRadius,
                                        "&:hover":{
                                            borderBlockColor:theme.palette.success.main
                                        }
                                        }}
                                    />
            </Styledstack>

            <Styledstack sx={{
            }}>
                <Button
                    variant="contained" 
                    sx={{
                    width:"100%",
                    "&:hover":{
                        backgroundColor:theme.palette.secondary.main,
                    }
                    }}
                    onClick={resetPasswordModelOn}
                    >
                        <Typography variant='h6'>Recovery Password</Typography>
                    </Button>
                       {reset&& <Reset reset={reset} setReset={setReset} />}
            </Styledstack>
        </Stack>



    </Stack>,
    document.getElementById("portal-recovery")
  )
}
