import { createTheme } from '@mui/material'

export default function Theme() {

    const theme=createTheme({
        palette:{

            mode:"light",

            primary:{
                main:'#032547',
                light:'#072e54',
                dark:'#021630',
            },
            secondary:{
                main:'#0f79c5',
                ligh:'#118ad9',
                dark:'#0f59a3',
            },
            warning:{
                main:'#cb5108',
                light:'#d6690a',
                dark:'#dc770d',
            },
            error:{
                main:'#c82625',
                light:'#da312a',
                dark:'#bb1f1e',
            },
            success:{
                main:'#1acf3a',
                light:'#5ed964',
                dark:'#00b500',
            },
            background:{
                paper:'white',
                normal:'white',
            }

        },
        shape:{
            borderRadius:5
        }
    })
  return theme;
}
