import { Button, Stack, Typography } from '@mui/material'
import React from 'react'
import Theme from './Theme'
import styled from '@emotion/styled';
import ReactDOM from 'react-dom'

const theme=Theme();

const Overly=styled(Stack)(({})=>({
    position:"fixed",
    top:0,
    left:0,
    right:0,
    bottom:0,
    backgroundColor:'rgba(0,0,0, 0.7)',
    zIndex:1000
}))

const ModelStack=styled(Stack)(({})=>({
    zIndex:1000,

}))


export default function SuccessAlert({setAlert}) {

  return ReactDOM.createPortal(
    <Overly>
        <Stack justifyContent="center" alignItems="center" sx={{

        }}>
            <ModelStack
            sx={{
                marginTop:45,
                marginBottom:45,
                backgroundColor:theme.palette.background.paper,
                width:370,
                height:175,
                borderRadius:theme.shape.borderRadius*0.5,
                overflow:"hidden"
                }}
            >
                <Stack textAlign="center" alignItems="center" spacing={3}>
                    <Typography>Successfully Updated</Typography>
                    <CheckIcon fontSize="inherit" />
                    <Button variant='contained' sx={{width:"20%"}} onClick={()=>setAlert()}><Typography variant='h6'>Ok</Typography></Button>
                </Stack>

            </ModelStack>
        </Stack>
    </Overly>,
    document.getElementById("portal-success")
  )
}
